mapper.select:
SELECT a.*,TYPE,image,sum_score,sum_people,sum_score/sum_people score
FROM movie a LEFT JOIN movie_image b ON a.movie_id=b.movie_id
and type='cover_type' 
LEFT JOIN score c ON a.movie_id=c.movie_id

	order by ${example.orderByClause}


	limit #{limit}

SELECT a.*,TYPE,image,sum_score,sum_people,sum_score/sum_people score FROM movie a LEFT JOIN movie_image b ON a.movie_id=b.movie_id and type='cover_type' LEFT JOIN score c ON a.movie_id=c.movie_id
where name like #{kw1}
union SELECT a.*,TYPE,image,sum_score,sum_people,sum_score/sum_people score FROM movie a LEFT JOIN movie_image b ON a.movie_id=b.movie_id and type='cover_type' LEFT JOIN score c ON a.movie_id=c.movie_id
where foreign_name like #{kw2}
order by ${example.orderByClause}

